import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import styles from '../styles/LocationPicker.module.css'; // Importer le fichier CSS

const LocationPicker = ({ setLocation }) => {
  const [position, setPosition] = useState([51.505, -0.09]);

  const handleMapClick = (e) => {
    const { lat, lng } = e.latlng;
    setPosition([lat, lng]);
    setLocation({ lat, lng });
  };

  return (
    <div className={styles.mapContainer}> {/* Ajouter la classe de style */}
      <MapContainer
        center={position}
        zoom={13}
        style={{ height: "100%", width: "100%" }}
        onClick={handleMapClick}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
        />
        <Marker position={position}>
          <Popup className={styles.popup}>Emplacement sélectionné : {position.toString()}</Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default LocationPicker;
