const DescriptionForm = ({ capturedImage, handleSubmit }) => {
    const [reason, setReason] = useState("");
    const [date, setDate] = useState(new Date().toISOString().slice(0, 10)); // Date actuelle par défaut
  
    const onSubmit = (e) => {
      e.preventDefault();
      handleSubmit({ reason, date, capturedImage });
    };
  
    return (
      <form onSubmit={onSubmit}>
        <div>
          <label>Raison :</label>
          <textarea value={reason} onChange={(e) => setReason(e.target.value)} />
        </div>
        <div>
          <label>Date :</label>
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </div>
        <button type="submit">Valider</button>
      </form>
    );
  };
