import React, { useState, useRef } from 'react';
import { Camera } from 'lucide-react';

const videoConstraints = {
    width: 1280,
    height: 720,
    facingMode: "user"
};

const CameraCapture = ({ onCapture }) => {
    const videoRef = useRef(null);
    const [imageSrc, setImageSrc] = useState(null);

    const capture = () => {
        const canvas = document.createElement('canvas');
        canvas.width = videoConstraints.width;
        canvas.height = videoConstraints.height;
        canvas.getContext('2d').drawImage(videoRef.current, 0, 0);
        const imageSrc = canvas.toDataURL('image/jpeg');
        setImageSrc(imageSrc);
        onCapture(imageSrc);
    };

    return (
        <div className="w-full max-w-3xl mx-auto border rounded-lg shadow-lg">
            <div className="p-6">
                <div className="relative aspect-video mb-4">
                    <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        muted
                        className="w-full h-full object-cover rounded-lg"
                        style={{
                            transform: 'scaleX(-1)'
                        }}
                    />
                </div>
                <button 
                    onClick={capture} 
                    className="w-full py-2 px-4 bg-blue-500 text-white rounded hover:bg-blue-600 flex items-center justify-center"
                >
                    <Camera className="mr-2 h-4 w-4" />
                    Capture photo
                </button>
            </div>
        </div>
    );
};

export default CameraCapture;
