import React, { useState, useRef } from 'react';
import Webcam from 'react-webcam';
import './WebcamCapture.css'; // Styles si nécessaires

const WebcamCapture = ({ onCapture }) => {
  const webcamRef = useRef(null);
  const [imageSrc, setImageSrc] = useState(null);

  const capture = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setImageSrc(imageSrc);
    onCapture(imageSrc); // Passe l'image capturée au composant parent
  };

  return (
    <div className="webcam-container">
      <h1 className="title">Capturez la décharge</h1>
      <Webcam
        ref={webcamRef}
        audio={false}
        screenshotFormat="image/jpeg"
        className="webcam"
      />
      <button onClick={capture}>Prendre une photo</button>

      {imageSrc && (
        <div>
          <h2>Prévisualisation de la photo :</h2>
          <img src={imageSrc} alt="Capture" className="captured-image" />
        </div>
      )}
    </div>
  );
};

export default WebcamCapture;
