import React, { useState } from "react";
import CameraCapture from "../pages/CameraCapture";
import DescriptionForm from "../pages/DescriptionForm";
import LocationPicker from "../pages/LocationPicker";

const ReportPage = () => {
  const [capturedImage, setCapturedImage] = useState(null);
  const [location, setLocation] = useState(null);
  const [formData, setFormData] = useState({});

  const handleSubmit = (details) => {
    setFormData(details);
    // Ajoute la localisation à formData avant l'envoi
    const completeData = { ...details, location };
    
    // Envoie les données au backend
    fetch("/api/report", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(completeData),
    })
      .then((response) => {
        if (response.ok) {
          alert("Signalement confirmé !");
        } else {
          alert("Erreur lors de l'envoi du signalement.");
        }
      })
      .catch((error) => {
        console.error("Erreur lors de l'envoi :", error);
        alert("Échec de l'envoi. Veuillez réessayer.");
      });
  };

  return (
    <div>
      <h1>Signalement d'une décharge non collectée</h1>

      {/* Prise de photo */}
      <CameraCapture setCapturedImage={setCapturedImage} />
      {capturedImage && <img src={capturedImage} alt="Captured" width="200" />}

      {/* Formulaire de description */}
      {capturedImage && (
        <DescriptionForm capturedImage={capturedImage} handleSubmit={handleSubmit} />
      )}

      {/* Carte pour la sélection de la position */}
      {formData.reason && (
        <LocationPicker setLocation={setLocation} />
      )}

      {/* Validation */}
      {location && (
        <button onClick={() => handleSubmit(formData)}>Valider et Envoyer</button>
      )}
    </div>
  );
};

export default ReportPage;
