import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { auth, googleProvider, facebookProvider } from '../config/firebaseConfig';

import '../styles/LoginForm.css'; // Assurez-vous que le chemin est correct

const LoginPage = () => {
  const navigate = useNavigate(); // Initialise le hook de navigation

  const handleGoogleLogin = async () => {
    try {
      const result = await auth.signInWithPopup(googleProvider);
      console.log('Connexion réussie avec Google:', result.user);
      navigate('/dashboard'); // Redirection après connexion réussie
    } catch (error) {
      console.error('Erreur lors de la connexion avec Google:', error);
    }
  };

  const handleFacebookLogin = async () => {
    try {
      const result = await auth.signInWithPopup(facebookProvider);
      console.log('Connexion réussie avec Facebook:', result.user);
      navigate('/HomePage'); // Redirection après connexion réussie
    } catch (error) {
      console.error('Erreur lors de la connexion avec Facebook:', error);
    }
  };

  return (
    <div className="login-page-container">
      <h1 className="login-page-title">Bienvenue sur Recyclab ! Connectez-vous.</h1>
      <form className="login-page-form">
        <div className="login-form-group">
          <label htmlFor="identifier">Adresse e-mail ou numéro de tél</label>
          <input
            type="text"
            id="identifier"
            placeholder="Entrez votre email ou numéro de téléphone"
          />
        </div>
        <div className="login-form-group">
          <label htmlFor="password">Mot de passe</label>
          <input
            type="password"
            id="password"
            placeholder="Entrez votre mot de passe"
          />
        </div>
        <button type="submit" className="login-page-button">Se connecter</button>
      </form>

      <div className="login-social-buttons">
        <button className="login-google-button" onClick={handleGoogleLogin}>
          Se connecter avec Google
        </button>
        <button className="login-facebook-button" onClick={handleFacebookLogin}>
          Se connecter avec Facebook
        </button>
      </div>

      <div className="login-form-links">
        <Link to="/signup" className="login-create-account">Créer un compte?</Link>
        <span className="login-link-separator">•</span>
        <Link to="/forgot-password" className="login-forgot-password">Mot de passe oublié ?</Link>
      </div>
    </div>
  );
};

export default LoginPage;
